import React from "react";
import { useNavigate } from "react-router-dom";
import "./PlanSummary.css"; // ‰Ω†ÂèØ‰ª•‰∏∫Ëøô‰∏™È°µÈù¢ÂçïÁã¨ËÆæÂÆöÊ†∑Âºè

function PlanSummary() {
  const navigate = useNavigate();
  const stored = localStorage.getItem("myPlan");
  const parsed = stored ? JSON.parse(stored) : { schedule: [] };

  return (
    <div className="plan-summary-wrapper">
      <h2>üìÖ Your Travel Summary</h2>
      {parsed.schedule.map((day, i) => (
        <div className="day-summary-card" key={i}>
          <h3>{day.date}</h3>
          {day.items.map((item, j) => (
            <div className="summary-item" key={j}>
              {item.time && <div className="summary-time">üïê {item.time}</div>}
              {(item.locationName || item.title) && (
                <div className="summary-title">üìç {item.locationName || item.title}</div>
              )}
              {item.description && <div className="summary-desc">{item.description}</div>}
              {item.duration && <div className="summary-duration">‚è± {item.duration}</div>}
            </div>
          ))}
        </div>
      ))}
      <button onClick={() => navigate("/")}>‚Üê Back to Home</button>
    </div>
  );
}


export default PlanSummary;
