import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function NotificationList() {
  const { id } = useParams(); // ÂΩìÂâçÁî®Êà∑ ID
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    const fetchNotifications = async () => {
      // 1) ÂÖàÂ∞ùËØï‰ªéÊú¨Âú∞ÊàñË∑ØÁî±Êãø
      let userId = localStorage.getItem("userId") || id;
      console.log("ÂΩìÂâçÁî®Êà∑ID:", userId);

      // 2) ÈÉΩÊ≤°ÊúâÂ∞±Âéª‰ºöËØùÊé•Âè£Êãø‰∏ÄÊ¨°
      if (!userId) {
        try {
          const meRes = await fetch("http://localhost:3001/api/auth/me", {
            credentials: "include",
          });
          const ct = meRes.headers.get("content-type") || "";
          if (meRes.ok && ct.includes("application/json")) {
            const me = await meRes.json();
            if (me?.user?.id) {
              userId = String(me.user.id);
              localStorage.setItem("userId", userId);
            }
          }
        } catch (_) {}
      }

      if (!userId) return; // ‰ªçÁÑ∂Êãø‰∏çÂà∞Â∞±Áõ¥Êé•ËøîÂõû

      try {
        const res = await fetch(
          `http://localhost:3001/api/notification/user/${userId}`,
          { credentials: "include" }
        );
        const ct = res.headers.get("content-type") || "";
        if (!ct.includes("application/json")) {
          // ÈÅøÂÖçÊää HTML ÂΩì JSON
          console.warn("Expected JSON, got:", ct, "body:", await res.text());
          setNotifications([]);
          return;
        }
        const data = await res.json();
        setNotifications(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error("‚ùå Ëé∑ÂèñÈÄöÁü•Â§±Ë¥•", err);
      }
    };

    fetchNotifications();
  }, []); // ‚úÖ ‰øùÊåÅ‰Ω†ÁöÑÁ©∫‰æùËµñ

  const markAsRead = async (notificationId) => {
    try {
      await fetch(
        `http://localhost:3001/api/notification/${notificationId}/read`,
        {
          method: "PATCH",
          credentials: "include",
        }
      );

      setNotifications((prev) =>
        prev.map((n) => (n.id === notificationId ? { ...n, isRead: true } : n))
      );
    } catch (err) {
      console.error("‚ùå Ê†áËÆ∞‰∏∫Â∑≤ËØªÂ§±Ë¥•", err);
    }
  };

  return (
    <div className="p-6 bg-[#f9fbfd] min-h-screen">
      <h2 className="text-2xl font-bold mb-6">üîî ÈÄöÁü•ÂàóË°®</h2>

      {notifications.length === 0 ? (
        <p className="text-gray-500 text-lg">ÊöÇÊó†ÈÄöÁü•</p>
      ) : (
        <div className="space-y-4">
          {notifications.map((n) => (
            <div
              key={n.id}
              className={`bg-white rounded-2xl shadow-sm border px-6 py-4 transition duration-200 ${
                n.isRead ? "border-gray-300" : "border-yellow-400 bg-yellow-50"
              }`}
            >
              <div className="flex justify-between items-start">
                <div className="text-base font-medium text-gray-800">
                  {n.message}
                </div>
                <div className="text-sm text-gray-500 ml-4 whitespace-nowrap">
                  {new Date(n.createdAt).toLocaleString()}
                </div>
              </div>

              {!n.isRead && (
                <button
                  onClick={() => markAsRead(n.id)}
                  className="mt-4 px-4 py-2 rounded-xl text-white font-semibold hover:opacity-90 transition"
                >
                  Ê†áËÆ∞‰∏∫Â∑≤ËØª
                </button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
