import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./SummaryCard.css";
import Navbar from "./Navbar";

const SummaryCard = ({ city, departureTime, duration, coverImage, places, onClick }) => {
  return (
    <div className="summary-card" onClick={onClick}>
      <div className="summary-header">
        <div className="summary-title">{city}</div>
        <div className="summary-subinfo">
          <span>{departureTime}</span>„Éª<span>{duration}</span>
        </div>
      </div>
      <div className="summary-body">
        <div className="summary-place-list">
          {places.map((p, idx) => (
            <div key={idx} className="place-line">
              <span className="place-time">{p.time}</span>
              <span className="place-title">{p.title}</span>
            </div>
          ))}
        </div>
        <div className="summary-image-box">
          <img src={coverImage || "/images/default-cover.jpg"} className="summary-cover" />
        </div>
      </div>
    </div>
  );
};


export default function TravelSummaries() {
  const navigate = useNavigate();
  const [summaries, setSummaries] = useState([]);

  useEffect(() => {
    const stored = localStorage.getItem("myPlan");
    if (stored) {
      try {
        const parsed = JSON.parse(stored);
        if (parsed.schedule) {
          const mapped = parsed.schedule.map(day => ({
            date: day.date,
            items: day.items || [],
            cover: "/images/default-cover.jpg" // ‰Ω†ÂèØ‰ª•Ëá™ÂÆö‰πâÂ∞ÅÈù¢ÈÄªËæë
          }));
          setSummaries(mapped);
        }
      } catch (err) {
        console.error("‚ùå Failed to parse localStorage myPlan:", err);
      }
    }
  }, []);

  return (
    <>
      <Navbar />
      <div className="summary-wrapper">
        <h2 className="summary-title">üåê My Travel Summaries</h2>
        {summaries.length === 0 ? (
          <p style={{ padding: "1rem" }}>No summaries found.</p>
        ) : (
          summaries.map((summary, index) => (
            <SummaryCard
              key={index}
              date={summary.date}
              itemCount={summary.items.length}
              coverImage={summary.cover}
              onClick={() => navigate(`/plan-summary`)}
            />
          ))
        )}
      </div>
    </>
  );
}
