import { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";


export default function SplitwisePage() {
  const [tripList, setTripList] = useState([]);
  const [selectedTripId, setSelectedTripId] = useState("");
  const [selectedTrip, setSelectedTrip] = useState(null);
  const [members, setMembers] = useState([]);
  const [expenses, setExpenses] = useState([]);

  // Fetch trips and initial data
  useEffect(() => {
    axios.get("/api/trips").then(res => setTripList(res.data));
  }, []);

  useEffect(() => {
    if (selectedTripId) {
      axios.get(`/api/trips/${selectedTripId}`).then(res => setSelectedTrip(res.data));
      axios.get(`/api/members?tripId=${selectedTripId}`).then(res => setMembers(res.data));
      axios.get(`/api/expenses?tripId=${selectedTripId}`).then(res => setExpenses(res.data));
    }
  }, [selectedTripId]);

  const handleAddExpense = () => {
    setExpenses([...expenses, { name: "", amount: "", paidBy: "" }]);
  };

  const handleChange = (index, field, value) => {
    const newExpenses = [...expenses];
    newExpenses[index][field] = value;
    setExpenses(newExpenses);
  };

  const handleSave = async () => {
    await axios.post("/api/expenses/save", { tripId: selectedTripId, expenses });
    alert("保存成功！");
  };

  return (
    <>
    <Navbar />
    <SubNavBar />
    <div className="splitwise-page">
      <div className="trip-selector-global">
        <label><strong>查看行程：</strong></label>
        <select value={selectedTripId} onChange={(e) => setSelectedTripId(Number(e.target.value))}>
          <option value="">选择行程</option>
          {tripList.map(trip => (
            <option key={trip.id} value={trip.id}>
              {trip.fromCity} → {trip.destination}
            </option>
          ))}
        </select>
      </div>

      {selectedTrip && (
        <div className="splitwise-card">
          <h3>{selectedTrip.fromCity} → {selectedTrip.destination}</h3>
          <p>{selectedTrip.startDate} ~ {selectedTrip.endDate}</p>

          {expenses.map((item, index) => (
            <div className="budget-item" key={index}>
              <input
                value={item.name}
                onChange={(e) => handleChange(index, "name", e.target.value)}
                placeholder="项目名称"
              />
              <input
                type="number"
                value={item.amount}
                onChange={(e) => handleChange(index, "amount", e.target.value)}
                placeholder="金额"
              />
              <select
                value={item.paidBy}
                onChange={(e) => handleChange(index, "paidBy", e.target.value)}
              >
                <option value="">选择付款人</option>
                {members.map((member) => (
                  <option key={member.id} value={member.user.email}>
                    {member.user.name}
                  </option>
                ))}
              </select>
            </div>
          ))}

          <button onClick={handleAddExpense}>➕ 添加费用项</button>
          <button onClick={handleSave} className="save-btn">💾 保存</button>
        </div>
      )}
    </div>
    </>
  );
}
