import React from "react";
import { useNavigate } from "react-router-dom";
import "./SummaryCard.css"; // Add your styles here

const SummaryCard = () => {
  const navigate = useNavigate();
  const plan = JSON.parse(localStorage.getItem("myPlan"));

  return (
    <div className="summary-wrapper">
      <h2>üåê My Travel Summaries</h2>
      <div className="summary-list">
        {plan.schedule.map((day, i) => (
          <div
            className="summary-card"
            key={i}
            onClick={() => navigate("/plan-summary", { state: day })} // üëà pass selected day
          >
            <div className="summary-title">
              <strong>{day.date}</strong>
              <span>{day.items.length} items</span>
            </div>
            <img src="/images/placeholder.png" alt="cover" />
          </div>
        ))}
      </div>
    </div>
  );
};

export default SummaryCard;
