// Login.js
import React, { useState } from 'react';
import './Login.css';
import { useNavigate, Link } from 'react-router-dom';

// 普通 API 请求：用相对路径，开发走 setupProxy，生产走 Nginx 反代
const API_BASE = '';

const isDev =
  process.env.NODE_ENV === 'development' ||
  window.location.port === '3000'; // 兜底判断

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setMessage('');
    setLoading(true);
    try {
      // 账号密码登录 —— 一定要带 credentials
      const resp = await fetch(`/api/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ email, password }),
      });
      const data = await resp.json().catch(() => ({}));
      if (!resp.ok) {
        setMessage(`❌ 登录失败：${data?.error || 'unknown error'}`);
        setLoading(false);
        return;
      }

      // 再确认一次登录态并拿到 id
      const me = await fetch(`/api/auth/me`, { credentials: 'include' })
        .then(r => (r.ok ? r.json() : { user: null }))
        .catch(() => ({ user: null }));

      const id = me?.user?.id ?? data?.userId;
      if (id) localStorage.setItem('userId', String(id));

      setMessage('✅ 登录成功！');
      navigate('/');
    } catch (err) {
      console.error(err);
      setMessage('❌ 网络异常，请稍后重试');
    } finally {
      setLoading(false);
    }
  };

  // Google 登录：开发用 http://localhost:3001，生产用 /api（由 Nginx 反代）
  const handleGoogleSignIn = () => {
    const url = isDev
      ? 'http://localhost:3001/api/auth/google'
      : '/api/auth/google';
    window.location.assign(url); // 原生整页跳转，别用 <Link>/navigate
  };

  return (
    <div className="login-wrapper">
      <div className="login-card">
        <h1>Welcome GoGoGlobe！</h1>
        <p>Plan smart. Travel far. Remember forever.</p>

        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="E-mail Address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" disabled={loading}>
            {loading ? 'Signing in...' : 'Sign In'}
          </button>
        </form>

        <div className="message">{message}</div>

        <div className="divider">or</div>
        <button type="button" className="google-login" onClick={handleGoogleSignIn}>
          <img src="/images/web_light_rd_SI@1x.png" alt="Google" className="google-icon" />
        </button>

        <div className="register-hint">
          Do not have account？<Link to="/register">Create free account</Link>
        </div>
      </div>
    </div>
  );
}
