import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";
import "./TripPhoto.css";

export default function TripPhotoPage() {
  const [trips, setTrips] = useState([]);
  const [selectedTrip, setSelectedTrip] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [selectedDayIndex, setSelectedDayIndex] = useState(1);

  useEffect(() => {
    const fetchTrips = async () => {
      const userId = localStorage.getItem("userId");
      const res = await fetch(`http://localhost:3001/api/trip/user/${userId}`);
      const data = await res.json();
      setTrips(data);
    };
    fetchTrips();
  }, []);

  useEffect(() => {
    if (!selectedTrip) return;
    const fetchPhotos = async () => {
      const res = await fetch(`http://localhost:3001/api/photo/trip/${selectedTrip.id}`);
      const data = await res.json();
      setPhotos(data);
    };
    fetchPhotos();
  }, [selectedTrip]);

  const days = selectedTrip
    ? Math.ceil((new Date(selectedTrip.endDate) - new Date(selectedTrip.startDate)) / (1000 * 60 * 60 * 24)) + 1
    : 0;

  return (
    <>
      <Navbar />
      <SubNavBar />
      <div className="trip-photo-page">
        <h2 className="page-title">📷 My Travel Footprints</h2>

        <div className="trip-dropdown">
          <label>Choose Trip:</label>
          <select
            onChange={(e) => {
              const trip = trips.find((t) => t.id === parseInt(e.target.value));
              setSelectedTrip(trip);
              setSelectedDayIndex(1); // reset to Day 1
            }}
          >
            <option value="">-- Select a Trip --</option>
            {trips.map((t) => (
              <option key={t.id} value={t.id}>
                {t.fromCity} → {t.destination}
              </option>
            ))}
          </select>
        </div>

        {selectedTrip && (
          <div className="day-selector">
            <label>Select Day:</label>
            <select
              value={selectedDayIndex}
              onChange={(e) => setSelectedDayIndex(parseInt(e.target.value))}
            >
              {Array.from({ length: days }).map((_, i) => (
                <option key={i} value={i + 1}>
                  Day {i + 1}
                </option>
              ))}
            </select>
          </div>
        )}

        <div className="day-photo-wrapper">
          {selectedTrip && (
            <div className="photo-card">
              <div className="photo-card-content">
                <div className="left-column">
                  <h3 className="day-title">📅 Day {selectedDayIndex}</h3>
                  <div className="photo-grid">
                    {photos
                      .filter((p) => p.dayIndex === selectedDayIndex)
                      .map((p) => (
                        <div key={p.id} className="photo-item">
                          <img src={p.imageUrl} alt="" />
                          {p.placeName && <p>{p.placeName}</p>}
                        </div>
                      ))}
                  </div>
                </div>
                <div className="right-column">
                  <UploadPhoto
                    tripId={selectedTrip.id}
                    dayIndex={selectedDayIndex}
                    onUpload={(p) => setPhotos((prev) => [...prev, p])}
                  />
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
}

function UploadPhoto({ tripId, dayIndex, onUpload }) {
  const [files, setFiles] = useState([]);
  const [placeName, setPlaceName] = useState("");
  const [description, setDescription] = useState("");

  const handleUpload = async () => {
    const userId = localStorage.getItem("userId");
    for (const file of files) {
      const formData = new FormData();
      formData.append("photo", file);
      formData.append("tripId", tripId);
      formData.append("uploadedBy", userId);
      formData.append("dayIndex", dayIndex);
      formData.append("placeName", placeName);
      formData.append("description", description);

      const res = await fetch("http://localhost:3001/api/photo", {
  method: "POST",
  body: formData,
});

if (!res.ok) {
  const errText = await res.text();
  console.error("❌ 上传失败:", errText);
  alert("上传失败: " + errText);
  return;
}

      const newPhoto = await res.json();
      onUpload(newPhoto);
    }
    setFiles([]);
    setPlaceName("");
    setDescription("");
  };

  return (
    <div className="upload-form-card">
      <input
        type="file"
        multiple
        accept="image/*"
        onChange={(e) => setFiles(Array.from(e.target.files))}
      />
      <input
        type="text"
        placeholder="📍 Place name"
        value={placeName}
        onChange={(e) => setPlaceName(e.target.value)}
      />
      <input
        type="text"
        placeholder="📝 Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <button onClick={handleUpload} disabled={!files.length}>
        Upload
      </button>
    </div>
  );
}
