import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents, useMap } from 'react-leaflet';
import './MapView.css'; 
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-control-geocoder/dist/Control.Geocoder.css';
import 'leaflet-control-geocoder';
import * as LGeocoder from 'leaflet-control-geocoder';  // ðŸ‘ˆ è¿™è¡ŒåŠ ä¸Š

// ðŸ‘‡æ‰‹åŠ¨æ³¨å†Œ geocoder åˆ° L.Controlï¼ˆæœ‰äº›ç‰ˆæœ¬éœ€è¦ï¼‰
if (typeof L.Control.Geocoder === 'undefined') {
  L.Control.Geocoder = LGeocoder.Geocoder;
}
// ä¿®å¤é»˜è®¤å›¾æ ‡ä¸æ˜¾ç¤ºçš„é—®é¢˜
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

// ðŸ“æ·»åŠ  Geocoder æŽ§ä»¶
function GeocoderControl() {
  const map = useMap();

  useEffect(() => {
    if (!map) return;

    const geocoder = L.Control.geocoder({
      position: 'topleft',  // âœ… æŒ‡å®šä½ç½®ï¼ˆå¯é€‰é¡¹: topleft / topright / bottomleft / bottomrightï¼‰
      defaultMarkGeocode: true,
      placeholder: 'Search location...',
      geocoder: L.Control.Geocoder.nominatim(),
    }).addTo(map);

    return () => map.removeControl(geocoder);
  }, [map]);

  return null;
}


// ðŸ“å¯é€‰ç‚¹ç»„ä»¶
function LocationMarker({ onSelect }) {
  const map = useMap();
  const [position, setPosition] = useState(null);

  useEffect(() => {
    const handleClick = async (e) => {
      const { lat, lng } = e.latlng;
      setPosition([lat, lng]);

      try {
        const response = await fetch(`https://nominatim.openstreetmap.org/reverse?lat=${lat}&lon=${lng}&format=json&accept-language=en`);
        const data = await response.json();
        const locationName = data.display_name || "Unknown Location";
        onSelect(locationName);
      } catch (err) {
        console.error('Reverse geocoding failed:', err);
        onSelect("Unknown Location");
      }
    };

    map.on('click', handleClick); // âœ… ä¸ä½¿ç”¨ useMapEvents

    return () => {
      map.off('click', handleClick);
    };
  }, [map, onSelect]);

  return position ? <Marker position={position} /> : null;
}



export default function MapView({ onLocationSelected }) {
  return (
      <MapContainer center={[37.7749, -122.4194]} zoom={13} scrollWheelZoom={true} style={{ height: '100%', width: '100%' }}>
<TileLayer
  attribution='&copy; OpenStreetMap contributors'
  url="https://a.tile.openstreetmap.de/{z}/{x}/{y}.png"
/>
        <GeocoderControl />
        <LocationMarker onSelect={onLocationSelected} />
      </MapContainer>
  );
}
